// Remover arrays da mensagem
const text = $json.output.replace(/(\[[\s\S]*?\]\s*)|(")/g, "").trim();

const delaySeconds = 2;
const maxLength = 180;

// 1. Detecta links e substitui por placeholders
const urlRegex = /(https?:\/\/[^\s)]+)/g;
const links = [];

let processedText = text.replace(urlRegex, (match) => {
  links.push(match);
  return `<LINK_${links.length - 1}>`;
});

// 2. Captura frases mantendo os sinais finais
// Evita dividir n√∫meros como 5.000 (n√£o quebra se tiver d√≠gitos antes/depois)
const safeSentenceRegex = /(?:[^.!?]|(?<=\d)[.!?](?=\d))+[.!?]/g;
const sentences = processedText.match(safeSentenceRegex) || [];

// 3. Reagrupar respeitando o tamanho m√°ximo
const result = [];
let buffer = '';

for (const sentence of sentences) {
  if ((buffer + ' ' + sentence).trim().length <= maxLength) {
    buffer = (buffer + ' ' + sentence).trim();
  } else {
    if (buffer) result.push(buffer.trim());
    buffer = sentence.trim();
  }
}
if (buffer) result.push(buffer.trim());

// 4. Substitui os links de volta
const finalMessages = result.map(msg => {
  let restored = msg;
  links.forEach((link, i) => {
    restored = restored.replace(`<LINK_${i}>`, link);
  });
  return { message: restored, delay: delaySeconds };
});

return finalMessages;
